body {
    background:#E0E4E5;
    background-blend-mode: color;
}



nav {
    width: 100%;
    background: #0A0908;
    top: 0;
    left: 4px;
    right: 16px;
    position: fixed;
}

nav ul {
    display: flex;
    flex-direction: row;
    overflow: hidden;
    margin: 0;
    padding: 0;
}

nav ul li {
    list-style: none;
    flex-grow: 1;
    float: left;
    text-align: center;
    width: 16.6667%;
    /* fallback for non-calc() browsers */
    width: calc(100% / 6);
}

nav ul li a {
    display: block;
    text-decoration: none;
    color: #F2F4F3;
    padding: 15px 0;
}

.login {
    color: aqua;
}

nav ul li a:hover {
    background-color: #292D28;
    color: ivory;
}

nav ul li a.active {
    background-color: #292D28;
    color: ivory;
}

.img-responsive {
    padding-top: 5px;
    max-height: 35px;
}

.logohead {
    color: ivory;
    display: inline;
}
/* ::content */

.helper {
    color:#0A0908 ;
    font-size: 1.2em;
    width: 40%;
    margin-top: 150px;
    margin-left: 200px;
    float: left;

}
.helper h2,.helper a {
    padding: 5px 0px;
}

.vl{
    display: inline;
    padding-left: 5px;
